operation.add:
// Apply filters if module is enabled
$filter_data = array();

if ($this->config->get('module_oc_filter_status')) {
    if ($this->config->get('module_oc_filter_price')) {
        if (isset($this->request->get['price_min'])) {
            $filter_data['filter_price_min'] = $this->request->get['price_min'];
        }
        if (isset($this->request->get['price_max'])) {
            $filter_data['filter_price_max'] = $this->request->get['price_max'];
        }
    }

    if ($this->config->get('module_oc_filter_brand') && isset($this->request->get['brands'])) {
        $filter_data['filter_manufacturer_id'] = explode(',', $this->request->get['brands']);
    }

    if ($this->config->get('module_oc_filter_attributes')) {
        foreach ($this->request->get as $key => $value) {
            if (strpos($key, 'attr_') === 0 && $value) {
                $attr_id = str_replace('attr_', '', $key);
                $filter_data['filter_attributes'][$attr_id] = explode(',', $value);
            }
        }
    }

    if ($this->config->get('module_oc_filter_metakeywords') && isset($this->request->get['metakeywords'])) {
        $filter_data['filter_metakeywords'] = explode(',', $this->request->get['metakeywords']);
    }
}

operation.search:
if (!empty($data['filter_category_id'])) {
			if (!empty($data['filter_sub_category'])) {
				$sql .= " AND cp.category_id IN (SELECT category_id FROM " . DB_PREFIX . "category_path WHERE path_id = '" . (int)$data['filter_category_id'] . "')";
			} else {
				$sql .= " AND cp.category_id = '" . (int)$data['filter_category_id'] . "'";
			}
		}

operation.add:

if (!empty($data['filter_price_min'])) {
    $sql .= " AND p.price >= '" . (float)$data['filter_price_min'] . "'";
}

if (!empty($data['filter_price_max'])) {
    $sql .= " AND p.price <= '" . (float)$data['filter_price_max'] . "'";
}

if (!empty($data['filter_manufacturer_id'])) {
    $sql .= " AND p.manufacturer_id IN (" . implode(',', array_map('intval', $data['filter_manufacturer_id'])) . ")";
}

if (!empty($data['filter_attributes'])) {
    foreach ($data['filter_attributes'] as $attribute_id => $attribute_values) {
        $sql .= " AND p.product_id IN (SELECT pa.product_id FROM " . DB_PREFIX . "product_attribute pa WHERE pa.attribute_id = '" . (int)$attribute_id . "' AND pa.text IN ('" . implode("','", array_map(array($this->db, 'escape'), $attribute_values)) . "'))";
    }
}

if (!empty($data['filter_metakeywords'])) {
    $keywords = array_map(array($this->db, 'escape'), $data['filter_metakeywords']);
    $sql .= " AND pd.meta_keyword REGEXP '" . implode('|', $keywords) . "'";
}

// Auto-register oc_filter module
if ($type == 'module') {
    $query = $this->db->query("SELECT * FROM " . DB_PREFIX . "extension WHERE `type` = 'module' AND `code` = 'oc_filter'");
    if (!$query->num_rows) {
        $this->db->query("INSERT INTO " . DB_PREFIX . "extension SET `type` = 'module', `code` = 'oc_filter'");
    }
}

// Auto-add oc_filter permissions for admin group
if (isset($data['user_group_id']) && $data['user_group_id'] == 1) {
    $data['permission']['access'][] = 'extension/module/oc_filter';
    $data['permission']['modify'][] = 'extension/module/oc_filter';
}

// Load filter module if enabled
if ($this->config->get('module_oc_filter_status')) {
    $data['oc_filter'] = $this->load->controller('extension/module/oc_filter');
}

